import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VisualizationContainer = () => {
  const [points, setPoints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [scale, setScale] = useState(30);

  const width = 500;
  const height = 500;

  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  const handleXClick = () => {
    if (points.length > 0) {
      axios
        .post('http://localhost:8000/rotate_points', {
          type: 'x',
          newPoints: points,
        })
        .then((response) => {
          if (response.status === 200) {
            setPoints(response.data.rotatedPoints);
          } else {
            throw new Error('Failed to rotate points!');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  const handleWheel = (e) => {
    e.preventDefault()
    const { deltaY } = e
    console.log(deltaY);
    if ((scale + deltaY) > scale && (scale - (0.075 * Math.abs(deltaY))) > 0) {
      setScale(scale - (0.075 * Math.abs(deltaY)))
    }
    if ((scale + deltaY) < scale) {
      setScale(scale + (0.075 * Math.abs(deltaY)))
    }
  }

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>Visualization</h1>
      <div id="svg-wrapper" onWheel={handleWheel} options={{ passive: false }}>
        <svg width={width} height={height} style={{ border: '1px solid black' }}>
          {points.map((point, i) => {
            const [x, y, z] = point;
            const perspective = 500;

            const projectedX =
              (x * scale) / (1 + z * scale - perspective) + width / 2;
            const projectedY =
              (-y * scale) / (1 + z * scale - perspective) + height / 2;

            const radius = 1.5 * ((z * scale - perspective));

            return (
              <circle
                key={i}
                cx={projectedX}
                cy={projectedY}
                r={radius}
                fill="blue"
              />
            );
          })}
        </svg>
      </div>
      <button onClick={handleXClick}>Rotate X</button>
      <p>this is filler text this is filler text</p>
      <p>this is filler text this is filler text</p>
      <p>this is filler text this is filler text</p>
      <p>this is filler text this is filler text</p>
      <p>this is filler text this is filler text</p>
      <p>this is filler text this is filler text</p>
    </div>
  );
};

export default VisualizationContainer;
