import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VisualizationContainer = () => {
  const [points, setPoints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [scale, setScale] = useState(60);
  const [colour, setColour] = useState("#002fa7");
  const [xAxis, setXAxis] = useState(3.5);

  const width = 500;
  const height = 500;

  const [yValue, setYValue] = useState(height/2)
  const [zValue, setZValue] = useState(.95)

  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  const handleClick = (e) => {
    if (points.length > 0) {
      const typeOfRot = e.target.value
      axios
        .post('http://localhost:8000/rotate_points', {
          type: typeOfRot,
          newPoints: points,
        })
        .then((response) => {
          if (response.status === 200) {
            setPoints(response.data.rotatedPoints);
          } else {
            throw new Error('Failed to rotate points!');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  const handleWheel = (e) => {
    const { deltaY } = e;
    const newScale = 
      deltaY > 0 
        ? scale - (0.075 * Math.abs(deltaY))
        : scale + (0.075 * Math.abs(deltaY));

    if (newScale > 1) {
      setScale(newScale);
    } else {
      setScale(1);
    }
  }

  const disableScroll = (e) => {
    // e.preventDefault();
  }

  const handleDrag = (e) => {
    // console.log(e.target)
  }

  const handleMouseDown =  (e) => {
    console.log("Mouse down: ", e.clientX);
  }

  const handleMouseMove =  (e) => {
    // console.log(e.target);
  }

  const handleMouseUp = (e) => {
    console.log("Mouse up: ", e.clientX);
  }

  const safeRadius = (z, scale, cameraPos) => {
    const denominator = 1 + z * scale / cameraPos;

    if (denominator <= 0.1) {
      return 0;
    }

    return 1.5 * (1 / denominator);
  };

  const rotatePoint = (point, angle, axis) => {
    const [x, y, z] = point;
    const cos = Math.cos(angle);
    const sin = Math.sin(angle);

    switch (axis) {
      case 'x':
        return [x, y * cos - z * sin, y * sin + z * cos];
      case 'y':
        return [x * cos + z * sin, y, -x * sin + z * cos];
      case 'z':
        return [x * cos - y * sin, x * sin + y * cos, z];
      default:
        return point;
    }
  };

  const handleAxesRot = (e) => {
    if (points.length > 0) {
      const typeOfRot = e.target.value;
      const angle = Math.PI / 180; // Example rotation angle in radians

      const newAxes = rotatePoint([xAxis, yValue, 0], angle, typeOfRot);
      setXAxis(newAxes[0]);
      setYValue(newAxes[1]);

      axios
        .post('http://localhost:8000/rotate_points', {
          type: typeOfRot,
          newPoints: [[newAxes[0], newAxes[1], 0]],
        })
        .then((response) => {
          if (response.status === 200) {
            const rotatedPoints = response.data.rotatedPoints;
            setPoints(rotatedPoints);
          } else {
            throw new Error('Failed to rotate points!');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div onWheel={disableScroll} style={{overflow: 'hidden'}} options={{ passive: false }}>
      <h1>Visualization</h1>
      <div id="svg-wrapper"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        <svg
          onWheel={handleWheel}
          width={width}
          height={height}
          style={{ border: '1px solid black' }}
        >
          {points.map((point, i) => {
            const [x, y, z] = point;
            const cameraPos = 500

            const projectedX = (x * scale * cameraPos) / (z * scale + cameraPos) + width / 2
            const projectedY = (-y * scale * cameraPos) / (z * scale + cameraPos) + height / 2

            const radius = safeRadius(z, scale, cameraPos);

            return (
              <circle
                key={i}
                cx={projectedX}
                cy={projectedY}
                r={radius}
                fill={colour}
              />
            );
          })}
          {/* X Axis */}
          <line x1={-width / 2} y1={0 + height / 2} x2={width / 2} y2={0 + height / 2} stroke='red' />
          {/* Y Axis */}
          <line x1={0 + width / 2} y1={-height / 2} x2={0 + width / 2} y2={height / 2} stroke='green' />
          {/* Z Axis (projected) */}
          <line x1={(-xAxis * scale * 500) / (zValue * scale + 500) + width / 2} y1={-yValue * scale + height / 2} x2={(-xAxis * scale * 500) / (zValue * scale + 500) + width / 2} y2={yValue * scale + height / 2} stroke='blue' />

          <line x1={(-xAxis * scale * 500) / (zValue * scale + 500) + width / 2} y1={-yValue * scale + height/2} x2={(xAxis * scale * 500) / (zValue * scale + 500) + width / 2} y2={yValue * scale + height/2} stroke='black' />
        </svg>
      </div>
      <button value="x" onClick={handleClick} >Rotate X</button>
      <button value="x" onClick={handleAxesRot}>Change Axis</button>
      <button value="y" onClick={handleClick}>Rotate Y</button>
      <button value="z" onClick={handleClick}>Rotate Z</button>
      <div id='testing'>
        <h1>TESTING</h1>
      </div>
    </div>
  );
};

export default VisualizationContainer;
