import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VisualizationContainer = () => {
  const [points, setPoints] = useState([]);
  const [rotatedPoints, setRotatedPoints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const width = 500;
  const height = 500;

  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  // useEffect(() => {
  //   axios
  //     .get('http://localhost:8000/rotated_points')
  //     .then((response) => {
  //       setRotatedPoints(response.data.rotatedPoints);
  //       setLoading(false);
  //     })
  //     .catch((error) => {
  //       console.error('Error fetching points:', error);
  //       setError('Failed to fetch points');
  //       setLoading(false);
  //     });
  // }, []);

  useEffect(() => {
    axios
      .post('http://localhost:8000/rotated_points', {
        type: "x",
        newPoints: points
      })
      .then(response => {
        if (!response.ok) {
          throw new Error("Failed to rotate points!")
        }
        console.log(response.data);

      })
      .catch(error => {
        console.log(error);

      })
  },[])

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>Visualization</h1>
      <svg width={width} height={height} style={{ border: '1px solid black' }}>
        {points.map((point, i) => {
          const [x, y, z] = point;
          const perspective = 500; // Adjust this for a more pronounced 3D effect
          const scale = 30; // Scaling factor for the size of the projection

          // Apply perspective projection: Points closer to the camera (z-value smaller) will appear larger
          const projectedX = (x * scale) / (1 + z * scale / perspective) + width / 2;
          const projectedY = (-y * scale) / (1 + z * scale / perspective) + height / 2;

          // Optional: Adjust radius of the circle to simulate 3D size
          const radius = 2 * (1 / (1 + z * scale / perspective)); // Radius decreases with depth

          return (
            <circle
              key={i}
              cx={projectedX}
              cy={projectedY}
              r={radius}
              fill="blue"
            />
          );
        })}
      </svg>
    </div>
  );
};

export default VisualizationContainer;
