import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Draggable from 'react-draggable';

const VisualizationContainer = () => {
  const [points, setPoints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [scale, setScale] = useState(60);
  const [colour, setColour] = useState("#002fa7")

  const width = 500;
  const height = 500;

  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  const handleXClick = () => {
    if (points.length > 0) {
      axios
        .post('http://localhost:8000/rotate_points', {
          type: 'x',
          newPoints: points,
        })
        .then((response) => {
          if (response.status === 200) {
            setPoints(response.data.rotatedPoints);
          } else {
            throw new Error('Failed to rotate points!');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  const handleWheel = (e) => {
    const { deltaY } = e
    if ((scale + deltaY) > scale && (scale - (0.075 * Math.abs(deltaY))) > 0) {
      setScale(scale - (0.075 * Math.abs(deltaY)))
    }
    if ((scale + deltaY) < scale) {
      setScale(scale + (0.075 * Math.abs(deltaY)))
    }
  }

  const handleDrag = (e) => {
    console.log(e.target.clientX, e.target.clientY);
  }

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>Visualization</h1>
      <Draggable>
      <div id="svg-wrapper" options={{ passive: false }}>
          <svg onDrag={handleDrag} onWheel={handleWheel} width={width} height={height} style={{ border: '1px solid black' }}>
            {points.map((point, i) => {
              const [x, y, z] = point;
              const cameraPos = 500

              const projectedX = (x * scale * cameraPos) / (z * scale + cameraPos) + width / 2
              const projectedY = (-y * scale * cameraPos) / (z * scale + cameraPos) + height / 2

              const radius = 1.5 * (1 / (1 + z * scale / cameraPos));

              return (
                <circle
                  key={i}
                  cx={projectedX}
                  cy={projectedY}
                  r={radius}
                  fill={colour}
                />
              );
            })}
          </svg>
      </div>
      </Draggable>
      <button onClick={handleXClick}>Rotate X</button>
      <div id='testing'>
        <h1>TESTING</h1>
      </div>
    </div>
  );
};

export default VisualizationContainer;
