import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VisualizationContainer = () => {
  const [points, setPoints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [scale, setScale] = useState(60);
  const [colour, setColour] = useState("#002fa7")

  const width = 500;
  const height = 500;

  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  const handleClick = (e) => {
    if (points.length > 0) {
      const typeOfRot = e.target.value
      axios
        .post('http://localhost:8000/rotate_points', {
          type: typeOfRot,
          newPoints: points,
        })
        .then((response) => {
          if (response.status === 200) {
            setPoints(response.data.rotatedPoints);
          } else {
            throw new Error('Failed to rotate points!');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  const handleWheel = (e) => {
    const { deltaY } = e;
    const newScale = 
      deltaY > 0 
        ? scale - (0.075 * Math.abs(deltaY))
        : scale + (0.075 * Math.abs(deltaY));

    if (newScale > 1) {
      setScale(newScale);
    } else {
      setScale(1);
    }
  }

  const disableScroll = (e) => {
    // e.preventDefault();
  }

  const handleDrag = (e) => {
    // console.log(e.target)
  }

  const handleMouseDown =  (e) => {
    console.log("Mouse down: ", e.clientX);
  }

  const handleMouseMove =  (e) => {
    // console.log(e.target);
  }

  const handleMouseUp = (e) => {
    console.log("Mouse up: ", e.clientX);
  }

  const safeRadius = (z, scale, cameraPos) => {
    const denominator = 1 + z * scale / cameraPos;

    if (denominator <= 0.1) {
      return 0;
    }

    return 1.5 * (1 / denominator);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div onWheel={disableScroll} style={{overflow: 'hidden'}} options={{ passive: false }}>
      <h1>Visualization</h1>
      <div id="svg-wrapper"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        <svg
          onWheel={handleWheel}
          width={width}
          height={height}
          style={{ border: '1px solid black' }}
        >
          {points.map((point, i) => {
            const [x, y, z] = point;
            const cameraPos = 500

            const projectedX = (x * scale * cameraPos) / (z * scale + cameraPos) + width / 2
            const projectedY = (-y * scale * cameraPos) / (z * scale + cameraPos) + height / 2

            const radius = safeRadius(z, scale, cameraPos);

            return (
              <circle
                key={i}
                cx={projectedX}
                cy={projectedY}
                r={radius}
                fill={colour}
              />
            );
          })}
          <line x1={(-2.5 * scale * cameraPos) / (z * scale + cameraPos) + width / 2} x2={width/2 + (z * scale * 500)} y2={height / 2} stroke='black' />
          {/* <line x1={(-2.5 * scale * 500) / (0.54 * scale + 500) + width / 2} y1={0 + height/2} x2={(2.5 * scale * 500) / (0.54 * scale + 500) + width / 2} y2={0 + height/2} stroke='black' /> */}
        </svg>
      </div>
      <button value="x" onClick={handleClick}>Rotate X</button>
      <button value="y" onClick={handleClick}>Rotate Y</button>
      <button value="z" onClick={handleClick}>Rotate Z</button>
      <div id='testing'>
        <h1>TESTING</h1>
      </div>
    </div>
  );
};

export default VisualizationContainer;
