import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VisualizationContainer = () => {
  const [points, setPoints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [scale, setScale] = useState(60);
  const [colour, setColour] = useState('#002fa7');
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });

  const width = 500;
  const height = 500;

  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  const handleXClick = () => {
    if (points.length > 0) {
      axios
        .post('http://localhost:8000/rotate_points', {
          type: 'x',
          newPoints: points,
        })
        .then((response) => {
          if (response.status === 200) {
            setPoints(response.data.rotatedPoints);
          } else {
            throw new Error('Failed to rotate points!');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  const handleWheel = (e) => {
    const { deltaY } = e;
    setScale((prevScale) => {
      const newScale = deltaY < 0 ? prevScale * 1.1 : prevScale / 1.1;
      return Math.max(10, Math.min(newScale, 200)); // Clamp scale between 10 and 200
    });
  };

  const handleMouseDown = (e) => {
    setDragging(true);
    setStartPos({ x: e.clientX, y: e.clientY });
  };

  const handleMouseMove = (e) => {
    if (dragging) {
      const deltaX = e.clientX - startPos.x;
      const deltaY = e.clientY - startPos.y;

      setOffset((prevOffset) => ({
        x: prevOffset.x + deltaX,
        y: prevOffset.y + deltaY,
      }));
      setStartPos({ x: e.clientX, y: e.clientY });
    }
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>Visualization</h1>
      <div id="svg-wrapper" options={{ passive: false }}>
        <svg
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onWheel={handleWheel}
          width={width}
          height={height}
          style={{ border: '1px solid black', cursor: dragging ? 'grabbing' : 'grab' }}
        >
          {/* Apply the offset as a group transformation */}
          <g transform={`translate(${offset.x}, ${offset.y})`}>
            {points.map((point, i) => {
              const [x, y, z] = point;
              const cameraPos = 500;

              const projectedX = (x * scale * cameraPos) / (z * scale + cameraPos) + width / 2;
              const projectedY = (-y * scale * cameraPos) / (z * scale + cameraPos) + height / 2;

              const radius = 1.5 * (1 / (1 + z * scale / cameraPos));

              return (
                <circle
                  key={i}
                  cx={projectedX}
                  cy={projectedY}
                  r={radius}
                  fill={colour}
                />
              );
            })}
          </g>
        </svg>
      </div>
      <button onClick={handleXClick}>Rotate X</button>
    </div>
  );
};

export default VisualizationContainer;
