import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { debounce, throttle } from 'lodash'

const VisualizationContainer = () => {
  // general variables
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // function display variables
  const [scale, setScale] = useState(100);
  const [points, setPoints] = useState([]);
  const [colour, setColour] = useState("#002fa7");

  // axes related variables
  const [xAxis, setXAxis] = useState([[3.5, 0, 0], [-3.5, 0, 0]]);
  const [yAxis, setYAxis] = useState([[0, 3.5, 0], [0, -3.5, 0]]);
  const [zAxis, setZAxis] = useState([[0, 0, 3.5], [0, 0, -3.5]]);

  // xy border lines
  const [posXBorder, setPosXBorder] = useState([[3.5, 3.5, 0], [3.5, -3.5, 0]])
  const [negXBorder, setNegXBorder] = useState([[-3.5, 3.5, 0], [-3.5, -3.5, 0]])
  const [posYBorder, setPosYBorder] = useState([[3.5, 3.5, 0], [-3.5, 3.5, 0]])
  const [negYBorder, setNegYBorder] = useState([[3.5, -3.5, 0], [-3.5, -3.5, 0]])
  // future xy border improvement
  // const xyBorder = [[3.5, 3.5, 0], [3.5, -3.5, 0], [-3.5, 3.5, 0], [-3.5, -3.5, 0], [3.5, 3.5, 0], [-3.5, 3.5, 0], [3.5, -3.5, 0], [-3.5, -3.5, 0]]

  // containing cube
  // const 


  // function rotation
  const [dragging, setDragging] = useState(false);
  const [currentCoords, setCurrentCoords] = useState([0, 0])

  const width = 1000;
  const height = 1000;


  // retrieve the calculated points from the backend
  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  // soon to be deprecated handles rotations manually with button clicks
  const handleClick = (e) => {
    if (points.length > 0) {
      const typeOfRot = e.target.value
      if (typeOfRot === "x") {
        setPoints(handleRotations(.12, points, xAxis))
        setYAxis(handleRotations(.12, yAxis, xAxis))
        setZAxis(handleRotations(.12, zAxis, xAxis))
      }
      else if (typeOfRot === "y") {
        setPoints(handleRotations(.12, points, yAxis))
        setXAxis(handleRotations(.12, xAxis, yAxis))
        setZAxis(handleRotations(.12, zAxis, yAxis))
      }
      else {
        setPoints(handleRotations(.12, points, zAxis))
        setXAxis(handleRotations(.12, xAxis, zAxis))
        setYAxis(handleRotations(.12, yAxis, zAxis))
      }
    }
  };

  // allows for scroll to zoom in or out
  const handleWheel = (e) => {
    const { deltaY } = e;
    const newScale = 
      deltaY > 0 
        ? scale - (0.075 * Math.abs(deltaY))
        : scale + (0.075 * Math.abs(deltaY));

    if (newScale < 30) {
    }
    else if (newScale > 140) {
    }
    else {
      setScale(newScale);
    }
  }

  // function to unitize a vector
  const unitizeVector = (v) => {
    const x = v[0]
    const y = v[1]
    const z = v[2]

    const magnitude = Math.sqrt(x**2 + y**2 + z**2)

    const unitizedVector = [x / magnitude, y / magnitude, z / magnitude]

    return unitizedVector
  }

  // create the Rodrigues rotation matrix
  const createRotationMatrix = (ux, uy, uz, theta) => {
    const cos = Math.cos(theta)
    const sin = Math.sin(theta)
    const oneMinusCos = 1 - cos

    const R = [
      [cos + (ux**2 * oneMinusCos), ux * uy * oneMinusCos - uz * sin, ux * uz * oneMinusCos + uy * sin],
      [uy * ux * oneMinusCos + uz * sin, cos + uy**2 * oneMinusCos, uy * uz * oneMinusCos - ux * sin],
      [uz * ux * oneMinusCos - uy * sin, uz * uy * oneMinusCos + ux * sin, cos + uz**2 * oneMinusCos]
    ]

    return R
  }

  // point rotation calculations in the frontend
  const handleRotations = (theta, points, axis) => {
    const directionVector = [
      axis[1][0] - axis[0][0],
      axis[1][1] - axis[0][1],
      axis[1][2] - axis[0][2],
    ];
    const [ux, uy, uz] = unitizeVector(directionVector);

    const R = createRotationMatrix(ux, uy, uz, theta);

    return points.map(point => {
      const rotated = [0, 0, 0];
      for (let i = 0; i < 3; i++) {
        for (let j = 0; j < 3; j++) {
          rotated[i] += R[i][j] * point[j];
        }
      }
      return rotated;
    });
  };


  // starts coordinate capture on mouse down
  const handleMouseDown =  (e) => {
    console.log("Mouse down: ", e.clientX, e.clientY);
    setDragging(true)
  }

  // Safeguard to prevent division by zero or small values
  const safeCoordDiff = (value, minValue) => Math.abs(value) < minValue ? minValue : value;

  const handleMouseMove = (e) => {
    if (!dragging) return;

    if (animationFrame) cancelAnimationFrame(animationFrame);

    animationFrame = requestAnimationFrame(() => {
      const newCoords = [e.clientX, e.clientY];
      const coordDiff = [
        safeCoordDiff(newCoords[0] - currentCoords[0], 0.75),
        safeCoordDiff(newCoords[1] - currentCoords[1], 0.75),
      ];

      if (Math.abs(coordDiff[1]) > 2 && Math.abs(coordDiff[0]) <= 2) {
        const rotationX = Math.min(0.02, Math.max(-0.02, coordDiff[1] * 0.001));
        const theAxis = [[1, 0, 0], [-1, 0, 0]];

        const updatedState = {
          points: handleRotations(rotationX, points, theAxis),
          xAxis: handleRotations(rotationX, xAxis, theAxis),
          yAxis: handleRotations(rotationX, yAxis, theAxis),
          zAxis: handleRotations(rotationX, zAxis, theAxis),
        };

        // Batch state update
        setState((prevState) => ({
          ...prevState,
          ...updatedState,
        }));

        rotateBorders(rotationX, 0, theAxis, zAxis);
      } else if (Math.abs(coordDiff[0]) > 2 && Math.abs(coordDiff[1]) <= 2) {
        const rotationZ = Math.min(0.02, Math.max(-0.02, coordDiff[0] * 0.001));
        const theAxis = [[1, 0, 0], [-1, 0, 0]];

        const updatedState = {
          points: handleRotations(-rotationZ, points, zAxis),
          xAxis: handleRotations(-rotationZ, xAxis, zAxis),
          yAxis: handleRotations(-rotationZ, yAxis, zAxis),
        };

        // Batch state update
        setState((prevState) => ({
          ...prevState,
          ...updatedState,
        }));

        rotateBorders(0, -rotationZ, theAxis, zAxis);
      }

      setCurrentCoords(newCoords);
    });
  };  

  const handleMouseUp = (e) => {
    console.log("Mouse up: ", e.clientX, e.clientY);
    setDragging(false)
  } 

  const safeRadius = (z, scale, cameraPos) => {
    const denominator = 1 + z * scale / cameraPos;

    if (denominator <= 0.2) {
      return 0;
    }

    return 1.5 * (1 / denominator);
  };

  const projectPoint = ([x, y, z], cameraPos = 1000) => {
    return [
      (x * scale * cameraPos) / (z * scale + cameraPos) + width / 2,
      (-y * scale * cameraPos) / (z * scale + cameraPos) + height / 2,
    ];
  };

  const rotateBorders = (theta1, theta2, axis1, axis2) => {
    const posXRotation = handleRotations(theta1, posXBorder, axis1);
    const negXRotation = handleRotations(theta1, negXBorder, axis1);
    const posYRotation = handleRotations(theta1, posYBorder, axis1);
    const negYRotation = handleRotations(theta1, negYBorder, axis1);

    setPosXBorder(handleRotations(theta2, posXRotation, axis2));
    setNegXBorder(handleRotations(theta2, negXRotation, axis2));
    setPosYBorder(handleRotations(theta2, posYRotation, axis2));
    setNegYBorder(handleRotations(theta2, negYRotation, axis2));
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div style={{overflow: 'hidden'}} options={{ passive: false }}>
      <h1>Visualization</h1>
      <div id="svg-wrapper"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        <svg
          onWheel={handleWheel}
          width={width}
          height={height}
          style={{ border: '1px solid black' }}
        >
          {points.map((point, i) => {
            const [x, y, z] = point;
            const cameraPos = 500

            const [projectedX, projectedY] = projectPoint([x, y, z])

            const radius = safeRadius(z, scale, cameraPos);

            return (
              <circle
                key={i}
                cx={projectedX}
                cy={projectedY}
                r={radius}
                fill={colour}
              />
            );
          })}
          {[xAxis, yAxis, zAxis].map((axis, index) => {
            const [lineStart, lineEnd] = axis;
            const [x1, y1] = projectPoint(lineStart);
            const [x2, y2] = projectPoint(lineEnd);
            const axes = ["X", "Y", "Z"]
            const colours = ["#5A8AA8", "#9CA9B3", "#88BDBC"]

            return (
              <g key={index}>
                <line
                  x1={x1}
                  y1={y1}
                  x2={x2}
                  y2={y2}
                  stroke={colours[index]}
                  strokeWidth={3}
                />
                <text x={x1 - 5} y={y1 - 2} fontSize={scale / 2.5} fill="#333333" fontWeight="bold" style={{ userSelect: "none" }}>{axes[index]}</text>
              </g>
            )
          })}
          <line
            x1={projectPoint(posXBorder[0])[0]}
            y1={projectPoint(posXBorder[0])[1]}
            x2={projectPoint(posXBorder[1])[0]}
            y2={projectPoint(posXBorder[1])[1]}
            stroke='#D6D6D6'
          />
          <line
            x1={projectPoint(negXBorder[0])[0]}
            y1={projectPoint(negXBorder[0])[1]}
            x2={projectPoint(negXBorder[1])[0]}
            y2={projectPoint(negXBorder[1])[1]}
            stroke='#D6D6D6'
          />
          <line
            x1={projectPoint(posYBorder[0])[0]}
            y1={projectPoint(posYBorder[0])[1]}
            x2={projectPoint(posYBorder[1])[0]}
            y2={projectPoint(posYBorder[1])[1]}
            stroke='#D6D6D6'
          />
          <line
            x1={projectPoint(negYBorder[0])[0]}
            y1={projectPoint(negYBorder[0])[1]}
            x2={projectPoint(negYBorder[1])[0]}
            y2={projectPoint(negYBorder[1])[1]}
            stroke='#D6D6D6'
          />
        </svg>
      </div>
    </div>
  );
};

export default VisualizationContainer;
