import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VisualizationContainer = () => {
  // general variables
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // function display variables
  const [scale, setScale] = useState(60);
  const [points, setPoints] = useState([]);
  const [colour, setColour] = useState("#002fa7");

  // axes related variables
  const [xAxis, setXAxis] = useState([[3.5, 0, 0], [-3.5, 0, 0]]);
  const [yAxis, setYAxis] = useState([[0, 3.5, 0], [0, -3.5, 0]]);
  const [zAxis, setZAxis] = useState([[0, 0, 3.5], [0, 0, -3.5]]);

  // function rotation
  const [dragging, setDragging] = useState(false);
  const [startCoords, setStartCoords] = useState([])
  const [currentCoords, setCurrentCoords] = useState([])

  const width = 500;
  const height = 500;


  // retrieve the calculated points from the backend
  useEffect(() => {
    axios
      .get('http://localhost:8000/points')
      .then((response) => {
        setPoints(response.data.points);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching points:', error);
        setError('Failed to fetch points');
        setLoading(false);
      });
  }, []);

  // soon to be deprecated handles rotations manually with button clicks
  const handleClick = (e) => {
    if (points.length > 0) {
      const typeOfRot = e.target.value
      axios
        .post('http://localhost:8000/rotate_points', {
          type: typeOfRot,
          newPoints: points,
          theta: .2,
        })
        .then((response) => {
          if (response.status === 200) {
            setPoints(response.data.rotatedPoints);

            updateAxes(typeOfRot)
          } else {
            throw new Error('Failed to rotate points!');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };


  // allows for scroll to zoom in or out
  const handleWheel = (e) => {
    const { deltaY } = e;
    const newScale = 
      deltaY > 0 
        ? scale - (0.075 * Math.abs(deltaY))
        : scale + (0.075 * Math.abs(deltaY));

    if (newScale > 1) {
      setScale(newScale);
    } else {
      setScale(1);
    }
  }

  // point rotation calculations in the frontend
  const handleRotations = (typeOfRot, points, theta) => {
    for (point in points) {
      vector = [x, y, z]
    sin = math.sin(theta)
    cos = math.cos(theta)
    Rx = [
        [1, 0, 0],
        [0, cos, -sin],
        [0, sin, cos]
    ]

    Ry = [
        [cos, 0, sin],
        [0, 1, 0],
        [-sin, 0, cos]
    ]

    Rz = [
        [cos, -sin, 0],
        [sin, cos, 0],
        [0, 0, 1]
    ]
    }
  }

  // starts coordinate capture on mouse down
  const handleMouseDown =  (e) => {
    console.log("Mouse down: ", e.clientX, e.clientY);
    setStartCoords([e.clientX, e.clientY])
    setDragging(true)
  }

  let lastCall = 0;

  //
  const handleMouseMove = (e) => {
    if (dragging) {
      const now = Date.now();
      if (now - lastCall > 200) { // Throttle to 100ms
        lastCall = now;

        const newCoords = [e.clientX - startCoords[0], e.clientY - startCoords[1]];
        setCurrentCoords(newCoords);

        axios
          .post('http://localhost:8000/rotate_points', {
            type: "x",
            newPoints: points,
            theta: 1 / parseFloat(newCoords[0]),
          })
          .then((response) => {
            setPoints(response.data.rotatedPoints);
          });
      }
    }
  };

  const handleMouseUp = (e) => {
    console.log("Mouse up: ", e.clientX, e.clientY);
    setDragging(false)
  }

  const safeRadius = (z, scale, cameraPos) => {
    const denominator = 1 + z * scale / cameraPos;

    if (denominator <= 0.2) {
      return 0;
    }

    return 1.5 * (1 / denominator);
  };

  const updateAxes = (typeOfRot) => {
    axios.post('http://localhost:8000/rotate_points', {
      type: typeOfRot,
      newPoints: xAxis,
      theta: .2,
    }).then((response) => {
      setXAxis(response.data.rotatedPoints);
    });

    axios.post('http://localhost:8000/rotate_points', {
      type: typeOfRot,
      newPoints: yAxis,
      theta: .2,
    }).then((response) => {
      setYAxis(response.data.rotatedPoints);
    });

    axios.post('http://localhost:8000/rotate_points', {
      type: typeOfRot,
      newPoints: zAxis,
      theta: .2,
    }).then((response) => {
      setZAxis(response.data.rotatedPoints);
    });
  };

  const projectPoint = ([x, y, z], cameraPos = 500) => {
    return [
      (x * scale * cameraPos) / (z * scale + cameraPos) + width / 2,
      (-y * scale * cameraPos) / (z * scale + cameraPos) + height / 2,
    ];
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div style={{overflow: 'hidden'}} options={{ passive: false }}>
      <h1>Visualization</h1>
      <div id="svg-wrapper"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        <svg
          onWheel={handleWheel}
          width={width}
          height={height}
          style={{ border: '1px solid black' }}
        >
          {points.map((point, i) => {
            const [x, y, z] = point;
            const cameraPos = 500

            const [projectedX, projectedY] = projectPoint([x, y, z])

            const radius = safeRadius(z, scale, cameraPos);

            return (
              <circle
                key={i}
                cx={projectedX}
                cy={projectedY}
                r={radius}
                fill={colour}
              />
            );
          })}
          {[xAxis, yAxis, zAxis].map((axis, index) => {
            const [lineStart, lineEnd] = axis;
            const [x1, y1] = projectPoint(lineStart);
            const [x2, y2] = projectPoint(lineEnd);
            const axes = ["X", "Y", "Z"]
            const colours = ["#5A8AA8", "#9CA9B3", "#88BDBC"]

            return (
              <g key={index}>
                <line
                  x1={x1}
                  y1={y1}
                  x2={x2}
                  y2={y2}
                  stroke={colours[index]}
                  strokeWidth={3}
                />
                <text x={x1 - 5} y={y1 - 2} fontSize={20} fontWeight="bold" style={{ userSelect: "none" }}>{axes[index]}</text>
              </g>
            )
          })}
        </svg>
      </div>
      <button value="x" onClick={handleClick} >Rotate X</button>
      <button value="y" onClick={handleClick}>Rotate Y</button>
      <button value="z" onClick={handleClick}>Rotate Z</button>
      <div id='testing'>
        <h1>TESTING</h1>
      </div>
    </div>
  );
};

export default VisualizationContainer;
